script:
frappe.provide("erpnext.queries");
frappe.ui.form.on("Sales Invoice", "refresh", function (frm, cdt, cdn) {
    frm.set_query("item_code", "items", function() {
      return { 
        query: "erpnext.controllers.queries.item_query", 
        filters: frm.doc.billed_in !== "" ? {"billable_in": frm.doc.billed_in} : {"disabled": "No"} 
      };
    });
});

frappe.ui.form.on("Sales Invoice", "customer",
    function(frm) {
        if(frm.doc.customer){
	 frappe.call({
             "method": "frappe.client.get",
             args: {
                 doctype: "Customer",
                 name: frm.doc.customer
             },
             callback: function (data) {
                 frappe.model.set_value(frm.doctype,frm.docname, "report_preference",
                                                               data.message.report_preference)
             }
         })
	}
});

frappe.ui.form.on("Sales Invoice", "billed_in",
  function(frm) {
    frm.set_query("item_code", "items", function() {
      return { 
        query: "erpnext.controllers.queries.item_query", 
        filters: frm.doc.billed_in !== "" ? {"billable_in": frm.doc.billed_in} : {"disabled": "No"} 
      };
    });        
});

frappe.ui.form.on("Item", "item_group",
    function(frm) {
        frappe.call({
            "method": "frappe.client.get",
            args: {
                doctype: "Item Group",
                name: frm.doc.item_group
            },
            callback: function (data) {
                frappe.model.set_value(frm.doctype,frm.docname, "is_sales_item",data.message.is_sale)
                frappe.model.set_value(frm.doctype,frm.docname, "is_purchase_item",
                                                                              data.message.is_purchase)
                if(data.message.is_service_or_stock == 'Service'){
                    frappe.model.set_value(frm.doctype,frm.docname, "is_service_item",1)
                    frappe.model.set_value(frm.doctype,frm.docname, "is_stock_item",0) 
                }
                if(data.message.is_service_or_stock == 'Stock'){
                    frappe.model.set_value(frm.doctype,frm.docname, "is_service_item",0)
                    frappe.model.set_value(frm.doctype,frm.docname, "is_stock_item",1)
                    if(data.message.warehouse){
                           frappe.model.set_value(frm.doctype,frm.docname, "default_warehouse",
                                                                                  data.message.warehouse)
                    }
                }
            }
        })
    });

frappe.ui.form.on('Customer', {
 	refresh: function(frm) {
 		if(frm.doc.name && (frappe.user.has_role("IP Physician")||frappe.user.has_role("OP Physician"))){
 			frm.add_custom_button(__('View History'), function() {
 				frappe.route_options = {"patient": frm.doc.name}
 				frappe.set_route("medical_record");
 			 });
		};
 }
});

frappe.ui.form.on("Customer", "dob", function(frm) {
  if(frm.doc.dob && !frm.doc.age){
    today = new Date();
    birthDate = new Date(frm.doc.dob);
    age = today.getFullYear() - birthDate.getFullYear();
    m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
        age--;
    }
    frappe.model.set_value(frm.doctype,frm.docname, "age", age)
    frm.set_df_property("age", "read_only", 1);
  }else if(!frm.doc.age){
    frm.set_df_property("age", "read_only", 0);
  }
});

frappe.ui.form.on("Customer", "age", function(frm) {
  if(frm.doc.age){
    today = new Date()
    dob_yr = today.getFullYear() - frm.doc.age
    //dob_str = dob_yr+"-01-01"
    dob_month = today.getMonth()+1
    dob_str = dob_yr+"-"+dob_month+"-"+today.getDate()
    frappe.model.set_value(frm.doctype,frm.docname, "dob", new Date(dob_str))
    frm.set_df_property("age", "read_only", 1);
  }else{
    frm.set_df_property("age", "read_only", 0);
  }
});
